(2891, 'Good Bye Lenin!', '(2003)', 'Comedy, Drama, Romance', '7.7', 'In 1990, to protect his fragile mother from a fatal shock after a long coma, a young man must keep her from learning that her beloved nation of East Germany as she knew it has disappeared.', 'Wolfgang Becker', 'https://www.imdb.com/title/tt0301357/?ref_=adv_li_tt', 'https://m.media-amazon.com/images/M/MV5BMTI0MTg4NzI3M15BMl5BanBnXkFtZTcwOTE0MTUyMQ@@._V1_UY98_CR0,0,67,98_AL_.jpg', '2891.jpeg'),
(2892, 'Rushmore', '(1998)', 'Comedy, Drama, Romance', '7.7', 'The extracurricular king of Rushmore Preparatory School is put on academic probation.', 'Wes Anderson', 'https://www.imdb.com/title/tt0128445/?ref_=adv_li_tt', 'https://m.media-amazon.com/images/M/MV5BMjE2OTc3OTk2M15BMl5BanBnXkFtZTgwMjg2NjIyMDE@._V1.jpg', '2892.jpeg'),
(2893, 'As Good as It Gets', '(1997)', 'Comedy, Drama, Romance', '7.7', 'A single mother and waitress, a misanthropic author, and a gay artist form an unlikely friendship after the artist is assaulted in a robbery.', 'James L. Brooks', 'https://www.imdb.com/title/tt0119822/?ref_=adv_li_tt', 'https://m.media-amazon.com/images/M/MV5BNWMxZTgzMWEtMTU0Zi00NDc5LWFkZjctMzUxNDIyNzZiMmNjXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1.jpg', '2893.jpeg'),
