(3674, 'Defiance', '(I) (2008)', 'Action, Drama, History', '7.2', 'Jewish brothers in Nazi-occupied Eastern Europe escape into the Belarussian forests, where they join Russian resistance fighters, and endeavor to build a village, in order to protect themselves and about one thousand Jewish non-combatants.', 'Edward Zwick', 'https://www.imdb.com/title/tt1034303/?ref_=adv_li_tt', 'https://m.media-amazon.com/images/M/MV5BZjEyYjk4ZDQtZDhjMi00NGFkLWExM2UtZjgyMDMxODgwNjgyXkEyXkFqcGdeQXVyNTA4NzY1MzY@._V1.jpg', '3674.jpeg'),
(3675, 'Che: Part One', '(2008)', 'Biography, Drama, History', '7.2', 'In 1956, Ernesto \'Che\' Guevara and a band of Castro-led Cuban exiles mobilize an army to topple the regime of dictator Fulgencio Batista.', 'Steven Soderbergh', 'https://www.imdb.com/title/tt0892255/?ref_=adv_li_tt', 'https://m.media-amazon.com/images/M/MV5BM2JiNjE4YjItNTFhNS00ZmYyLWEwNzctNzJmYzNiYWYzOGM3XkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1.jpg', '3675.jpeg'),
(3676, 'Mongol', '(2007)', 'Action, Biography, Drama', '7.2', 'The story recounts the early life of Genghis Khan who was a slave before going on to conquer half the world in 1206.', 'Sergei Bodrov', 'https://www.imdb.com/title/tt0416044/?ref_=adv_li_tt', 'https://m.media-amazon.com/images/M/MV5BODlhNTk4ODYtOThlMy00N2E1LThkZDktZjZlMjE1YjM3YTgwL2ltYWdlXkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1.jpg', '3676.jpeg'),
(3677, 'Kingdom of Heaven', '(2005)', 'Action, Adventure, Drama', '7.2', 'Balian of Ibelin travels to Jerusalem during the Crusades of the 12th century, and there he finds himself as the defender of the city and its people.', 'Ridley Scott', 'https://www.imdb.com/title/tt0320661/?ref_=adv_li_tt', 'https://m.media-amazon.com/images/M/MV5BODczYmE3ZDYtZjIwNC00ZTJhLThkMGUtZDcyNzkyNWMyZDNiXkEyXkFqcGdeQXVyMTAwMzUyMzUy._V1.jpg', '3677.jpeg');
